mapper.select:
SELECT l.id, l.user_id AS userId, u.username AS userName, l.module, l.operation, l.description, l.ip, l.create_time AS createTime FROM operation_log l LEFT JOIN user u ON l.user_id = u.id WHERE l.is_deleted = 0
AND l.user_id = #{userId}
AND l.module = #{module}
AND l.operation = #{operation}
AND l.create_time >= #{startTime}
AND l.create_time <= #{endTime}
ORDER BY l.create_time DESC
